def codeCrack(keypad = [[1,2,3],[4,5,6],[7,8,9]],instructions = ['ULL','RRDDD','LURDL','UUUUD'], x = 1, y = 1):
    code = []
    #print("X: ", x)
    #print("Y: ", y)
    #print("Starting: " + str(keypad[y][x]))
    for line in instructions:
        goodNumber = keypad[y][x]
        for instruction in line:
            try:
                if instruction == 'U':
                    y -= 1
                    goodNumber = punchIt(x, y, keypad)
                elif instruction == 'R':
                    x += 1
                    goodNumber = punchIt(x, y, keypad)
                elif instruction == 'D':
                    y += 1
                    goodNumber = punchIt(x, y, keypad)
                elif instruction == 'L':
                    x -= 1
                    goodNumber = punchIt(x, y, keypad)
            except IndexError:
                for ind in keypad:
                    try:
                        x = ind.index(goodNumber)
                        y = keypad.index(ind)
                        break
                    except Exception as e:
                        pass
        #print("GoodNumber: " + str(goodNumber))
        code.append(goodNumber)
    return code

def punchIt(x, y, keypad):
    if x < 0 or y < 0:
        raise IndexError
    tmp = keypad[y][x]
    #print("Current: " + str(tmp))
    if tmp == None:
        raise IndexError
    return tmp

def main():
    userInstructions = ['','','','','']
    #code = codeCrack(['','','','',''])
    demo = ['ULL','RRDDD','LURDL','UUUUD']
    #code = codeCrack()
    keypad2 = [[None,None,'1',None,None],[None,'2','3','4',None],['5','6','7','8','9'],[None,'A','B','C',None],[None,None,'D',None,None]]
    #code2 = codeCrack(keypad2, x = 0, y = 2)
    code = codeCrack(instructions = userInstructions)
    code2 = codeCrack(keypad = keypad2, instructions = userInstructions, x= 0, y = 2)
    print(code)
    print(code2)


if __name__ == '__main__':
    main()
