using System.Windows.Forms;

namespace AdventOfCode
{
	class Day2
	{

		internal static void Run()
		{
			string input = @"



";

			int position = 5;

			string resultA = "";

			foreach (string s in input.Split('\n'))
			{
				for (int i = 0; i < s.Length; i++)
				{
					switch (s[i])
					{
						case 'U':
							if (position > 3)
								position -= 3;
							break;

						case 'D':
							if (position < 7)
								position += 3;
							break;

						case 'L':
							if (position % 3 != 1)
								position--;
							break;

						case 'R':
							if (position % 3 != 0)
								position++;
							break;
					}
				}
				resultA = resultA + position.ToString();
			}


			string[] keypad = {
				"       ",
				"   1   ",
				"  234  ",
				" 56789 ",
				"  ABC  ",
				"   D   ",
				"       " };

			int x = 1;
			int y = 3;

			string resultB = "";

			foreach (string s in input.Split('\n'))
			{
				for (int i = 0; i < s.Length; i++)
				{
					switch (s[i])
					{
						case 'U':
							if (keypad[y - 1][x] != ' ')
								y--;
							break;

						case 'D':
							if (keypad[y + 1][x] != ' ')
								y++;
							break;

						case 'L':
							if (keypad[y][x - 1] != ' ')
								x--;
							break;

						case 'R':
							if (keypad[y][x + 1] != ' ')
								x++;
							break;
					}
				}
				resultB = resultB + keypad[y][x];
			}
			MessageBox.Show(resultA + '\n' + resultB);
		}

	}
}
