(ns pirfalt.advent_of_code_2016-day2-problem2
  (:require [clojure.string :as string]
            [clojure.test :as test]))


(def input "



\n")


(def moves
  {\U {3  -2,
       6  -4, 7  -4, 8  -4,
       10 -4, 11 -4, 12 -4,
       13 -2}
   \D {1  2,
       2  4, 3  4, 4  4,
       6  4, 7  4, 8  4,
       11 2,}
   \L {3 -1, 4 -1, 6 -1, 7 -1, 8 -1, 9 -1, 11 -1, 12 -1}
   \R {2  1, 3  1, 5  1, 6  1, 7  1, 8  1, 10  1, 11  1}})

(defn move [seed direction]
  (+ seed (get-in moves [direction seed] 0)))

(defn next-number [code line]
  (conj code (reduce move (last code) line)))
; (next-number [5] "ULL") -> [5 1]

(defn seek-code [str]
  (next (reduce next-number [5] (string/split-lines str))))

(defn code->string [code]
  (string/upper-case (string/join (map #(Integer/toHexString %) code))))


; (code->string (seek-code "ULL
; RRDDD
; LURDL
; UUUUD"))

(print (code->string (seek-code input)))
